/**
 * Gets localization files for a customer account
 *
 * Example:
 *    var params = {
 *      "environment": "prod",
 *      "customerId": 1475,
 *      "workflow": "prod",
 *      "catalogId": 7
 *    };
 *    getCustomerLocalization(params, function (err, locales) {
 *      if (err) {
 *        return console.log(err, err.stack);
 *      }
 *      console.log(JSON.stringify(locales, null, 2));
 *    });
 *
 * Outputs:
 *
 *     [
 *      {
 *        "Key": "prod/prod/customers/c1475/configureHtml/galleries/g_7/Fi18n_gallery_deDE_jsonp.js",
 *        "LastModified": "2014-03-20T22:49:31.000Z",
 *        "ETag": "8d9b1e290cfc949d4e26f0a11db11144",
 *        "Size": 213650,
 *        "locale": "deDE",
 *        "environment": "prod",
 *        "customerId": 1475,
 *        "workflow": "prod",
 *        "catalogId": 7
 *      }
 *     ]
 */


var listLocalizedObjects = require("../s3").listLocalizedObjects,
  _ = require("lodash"),
  default_options = {
    type: "customerLocalization"
  };

module.exports = function (options, callback) {
  listLocalizedObjects(
    _.defaults({}, options, default_options),
    {
      Prefix: options.environment + "/" +
        options.workflow +
        "/customers/c" + options.customerId +
        "/configureHtml/localization/Fi18n_configure_"
    },
    callback
  );
};
