var fs = require("fs");

module.exports = function (json_filename, callback) {
  fs.readFile(json_filename, function (err, data) {
    if (err) { return callback(err); }
    setImmediate(function () {
      try {
        callback(err, JSON.parse("" + data));
      } catch (e) {
        callback(e, false);
      }
    });
  });
};
