var path = require("path"),
  nixt = require("nixt"),
  binaryPath = path.normalize(__dirname + "/../bin/fluid-configurations");

describe("Fluid Configurations", function () {

  it("should show help", function (done) {
    nixt()
      .run(binaryPath + " --help")
      .stdout(/Usage: fluid-configurations \[index,download,process,upload,sync,help\] \[options\]/)
      .end(done);
  });

});
